import React from "react";
import { connect } from "react-redux";
import {
  addItem,
  clearItemFromCart,
  removeItem,
} from "../../redux/cart/cart.action";

import {
  ComponentContainer,
  Title,
  Price,
  ComponentSubcontainer,
  Img,
  ArrowsContainer,
  Quantity,
  Button,
} from "./checkout-payment.styles";

const CheckOutPaymentItem = ({ cartItem, addItem, clearItem, removeItem }) => {
  const { imageUrl, descriptionTitle, price, quantity } = cartItem;
  return (
    <div>
      <ComponentContainer className="paymentItem__container">
        <Title className="paymentItem__title">{descriptionTitle}</Title>
        <div className="paymentItem__price">{price}â‚¬</div>
        <ComponentSubcontainer className="paymentItem__subContainer">
          <Img alt="" src={imageUrl}></Img>
          <div className="paymentItem__arrowsContainer">
            <div
              className="paymentItem__arrow"
              onClick={() => removeItem(cartItem)}
            >
              &#10094;
            </div>
            <Price className="paymentItem__value">{quantity}</Price>
            <div
              className="paymentItem__arrow"
              onClick={() => addItem(cartItem)}
            >
              &#10095;
            </div>
          </div>
          <button
            onClick={() => clearItem(cartItem)}
            className="paymentItem__button"
          >
            Clear Item
          </button>
        </ComponentSubcontainer>
      </ComponentContainer>
    </div>
  );
};

const mapToDispatchToProps = (dispatch) => ({
  addItem: (item) => dispatch(addItem(item)),
  clearItem: (item) => dispatch(clearItemFromCart(item)),
  removeItem: (item) => dispatch(removeItem(item)),
});

export default connect(null, mapToDispatchToProps)(CheckOutPaymentItem);
