import React from "react";

import { ComponentContainer, Title } from "./scrolltop-button.styles";

const ScrollToTopButton = () => {
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: "smooth",
    });
  };

  return (
    <div onClick={scrollToTop} className="scroll__button">
      <span className="scroll__title">Back to top</span>
    </div>
  );
};

export default ScrollToTopButton;
