import styled from "styled-components";

export const CheckoutContainer = styled.div`
  display: flex;
  margin-top: 20px;
  margin-bottom: 20px;
`;

export const CheckoutImage = styled.img`
  object-fit: contain;
  width: 280px;
  height: 280px;
`;

export const CheckoutInfo = styled.div`
  padding-left: 20px;
  margin-top: 40px;
`;

export const CheckoutTitle = styled.p`
  font-size: 17px;
  font-weight: 800;
`;

export const CheckoutPrice = styled.p``;

export const CheckoutQuantity = styled.span`
  display: flex;
`;

export const CheckoutArrow = styled.div`
  cursor: pointer;
`;

export const CheckoutValue = styled.span`
  margin: 0 10px;
`;

export const CheckoutRating = styled.div`
  display: flex;
`;

export const CheckoutButton = styled.button`
  background: #f0c14b;
  border: 1px solid;
  margin-top: 10px;
  border-color: #a88734 #9c7e31 #846a29;
  color: #111;
  cursor: pointer;
`;
