import React from "react";

import Header from "../header/header.component";
import CollectionItem from "../collection-item/collection-item.component";

import { connect } from "react-redux";
import { selectCollection } from "../../redux/products/product.selectors";

import { CollectionOverviewContainer1 } from "./collection-overview.styles";

import "./collection-overview.styles.css";

const CollectionsOverviewContainer = ({ collection, history }) => {
  const { title, items } = collection;
  return (
    <>
      <Header />
      <div className="collectionOverview__pageContainer">
        <div className="collectionOverview__leftSide">
          LEFT SIDE
          <h3>Brand</h3>
          <input value="Submit" type="checkbox">
            Amazon Basics
          </input>
          <input type="checkbox">HC COLLECTION</input>
          <input type="checkbox">Amazon Essentials</input>
          <input type="checkbox">Cadcah</input>
          <input type="checkbox">Amazon Basic</input>
          <input type="checkbox">Addtam</input>
          <input type="checkbox">Champion</input>
        </div>
        <hr className="collectionOverview__verticalLine" />
        <div className="collectionOverview__center">
          CENTER SIDE
          <hr className="collectionOverview__horizontalLine" />
          <h2 className="collectionOverview__title">{}</h2>
          <div className="collectionOverview__items">
            {items.map((item) => (
              <CollectionItem key={item.id} item={item} history={history} />
            ))}
          </div>
        </div>
        <hr className="collectionOverview__verticalLine" />
        <div className="collectionOverview__rightSide">RIGTH SIDE</div>
      </div>
    </>
  );
};

const mapStateToProps = (state, ownProps) => {
  return {
    collection: selectCollection(ownProps.match.params.collectionId)(state),
  };
};

export default connect(mapStateToProps)(CollectionsOverviewContainer);
