import React from "react";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

import CarouselCard from "../carousel-card/carousel-card.component";
import CAROUSEL__DATA from "./multi-carousel.data";
import {
  CustomRightArrow,
  CustomLeftArrow,
} from "../custom-arrows/custom-arrows.component";

import { connect } from "react-redux";

import "./multi-carousel.styles.css";

const MultiCarousel = ({ carousel }) => {
  const responsive = {
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 5,
      slidesToSlide: 2, // optional, default to 1.
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
      slidesToSlide: 2, // optional, default to 1.
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
      slidesToSlide: 1, // optional, default to 1.
    },
  };

  console.log("carousel", carousel);
  return (
    <div>
      <Carousel
        containerClass="carousel__container"
        itemClass="carousel__item"
        sliderClass="carousel__slider"
        centerMode={true}
        showDots={true}
        dotListClass="carousel__dots"
        responsive={responsive}
        customRightArrow={<CustomRightArrow />}
        customLeftArrow={<CustomLeftArrow />}
      >
        {this.state.carousel.map((items) => {
          return console.log("aquiii", items);
        })}
      </Carousel>
    </div>
  );
};

const mapStateToProps = (state) => ({
  carousel: state,
});

export default connect(mapStateToProps)(MultiCarousel);

