import React from "react";

import MenuItem from "../menu-item/menu-item.component";

import "./directory.styles.css";

class Directory extends React.Component {
  constructor() {
    super();

    this.state = {
      sections: [
        {
          title: "Shop by Category",
          subtitle: "Shop now",
          //imageUrl: 'https://i.ibb.co/cvpntL1/hats.png',
          imageUrl:
            "https://images-na.ssl-images-amazon.com/images/G/01/AmazonExports/Fuji/2020/May/Dashboard/Fuji_Dash_Beauty_1x._SY304_CB432774351_.jpg",
          id: 1,
          linkUrl: "shop/hats",
        },
        {
          title: "Get fit at home",
          subtitle: "Explore now",
          //imageUrl: 'https://i.ibb.co/px2tCc3/jackets.png',
          imageUrl:
            "https://images-na.ssl-images-amazon.com/images/G/01/AmazonExports/Fuji/2020/May/Dashboard/Fuji_Dash_Electronics_1x._SY304_CB432774322_.jpg",
          id: 2,
          linkUrl: "shop/jackets",
        },
        {
          title: "AmazonBasics",
          subtitle: "See more",
          //imageUrl: 'https://i.ibb.co/0jqHpnp/sneakers.png',
          advert: "",
          imageUrl:
            "https://images-na.ssl-images-amazon.com/images/G/01/AmazonExports/Fuji/2021/January/Fuji_Dash_ClicktoShop_EN_v3__1x._SY304_CB661258542_.jpgs",
          id: 3,
          linkUrl: "shop/sneakers",
        },
        {
          title: "Oculus",
          subtitle: "Shop now",
          //imageUrl: 'https://i.ibb.co/GCCdy8t/womens.png',
          imageUrl:
            "https://images-na.ssl-images-amazon.com/images/G/01/AmazonExports/Fuji/2020/May/Dashboard/Fuji_Dash_PC_1x._SY304_CB431800965_.jpg",
          size: "large",
          id: 4,
          linkUrl: "shop/womens",
        },
        {
          title: "Computers & Accessories",
          subtitle: "Shop now",
          imageUrl:
            "https://images-na.ssl-images-amazon.com/images/G/01/AmazonExports/Fuji/2020/May/Dashboard/Fuji_Dash_PC_1x._SY304_CB431800965_.jpg",
          size: "large",
          id: 5,
          linkUrl: "shop/mens",
        },
        {
          title: "Gaming accessories",
          subtitle: "Shop now",
          imageUrl:
            "https://www.extremetech.com/wp-content/uploads/2017/11/PC-Gaming-640x353.jpg",
          size: "large",
          id: 6,
          linkUrl: "shop/mens",
        },
        {
          title: "Find your ideal TV",
          subtitle: "See more",
          imageUrl:
            "https://images-na.ssl-images-amazon.com/images/G/01/AmazonExports/Fuji/2020/May/Dashboard/Fuji_Dash_TV_2X._SY304_CB432517900_.jpg",
          size: "large",
          id: 7,
          linkUrl: "shop/mens",
        },
        {
          title: "Explore home bedding",
          subtitle: "See more",
          imageUrl:
            "https://images-na.ssl-images-amazon.com/images/G/01/AmazonExports/Fuji/2020/May/Dashboard/Fuji_Dash_HomeBedding_Single_Cat_1x._SY304_CB418596953_.jpg",
          size: "large",
          id: 8,
          linkUrl: "shop/mens",
        },
      ],
    };
  }

  render() {
    console.log("helol", this.state.sections[0]);
    return (
      <div className="directory-menu">
        <div className="first__row">
          {/* <div className="left__column"> */}
          <MenuItem props={this.state.sections[0]} />
          <MenuItem props={this.state.sections[1]} />
          <MenuItem props={this.state.sections[2]} />
          <MenuItem props={this.state.sections[3]} advert={advert} />
          {/* </div> */}
          {/* <div className="rigth__column">
            <div className="rightcolumn___top">
              Sign in for the best experience
            </div>
            <div className="rightcolumn___button">Anuncio</div>
          </div> */}
          <MenuItem props={this.state.sections[4]} />
          <MenuItem props={this.state.sections[5]} />
          <MenuItem props={this.state.sections[6]} />
          <MenuItem props={this.state.sections[7]} />
        </div>

        {/* <div className="second__row"> */}

        {/* </div> */}
      </div>
    );
  }
}

export default Directory;
