import React, { useState, useEffect } from "react";
import { useForm } from "react-hook-form";

import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { clearCart } from "../../redux/cart/cart.action";

import Popup from "../../components/popup/popup.component";
import HeaderImage1 from "../../assets/address-page-heade1.png";

import {
  CardElement,
  useStripe,
  useElements,
  CardNumberElement,
} from "@stripe/react-stripe-js";
import axios from "../../axios/axios";

import {
  selectCartItems,
  selectCartTotal,
} from "../../redux/cart/cart.selectors";
import { selectCurrentUser } from "../../redux/user/user.selectors";

import "./payment-page.styles.css";
import { createStructuredSelector } from "reselect";

import { db } from "../../firebase/firebase";

import styled from "styled-components";

const CardElementContainer = styled.div`
  margin-top: 20px;
  border: 1px solid rgba(34, 36, 38, 0.15);
  border-radius: 0.28571429rem;
  width: 496px;
  height: 40px;
  display: flex;
  align-items: center;
  & .StripeElement {
    width: 100%;
    padding: 15px;
  }
`;

const PaymentPage = ({ history, clearCart, currentUser, total, cart }) => {
  console.log("CARTTTTTTTTTTT", cart);
  const stripe = useStripe();
  const elements = useElements();
  const [error, setError] = useState(null);
  const [processing, setProcessing] = useState("");
  const [succeeded, setSucceeded] = useState(false);
  const [disabled, setDisabled] = useState(true);
  const [clientSecret, setClientSecret] = useState(true);
  const [popup, setPopup] = useState(false);
  const { register, handleSubmit } = useForm();

  const [dataForm, setDataForm] = useState("");

  useEffect(() => {
    //algo aqui
    const getClientSecret = async () => {
      const response = await axios({
        method: "post",
        url: `/payments/create?total=${total * 100}`,
      });
      setClientSecret(response.data.clientSecret);
    };
    getClientSecret();
  }, [total]);

  const handleChange = (event) => {
    console.log("dataForm", dataForm);
    const { name, value } = event.target;
    setDataForm({ ...dataForm, [name]: value });
  };

  const onFormSubmit = async (data) => {
    setDataForm(data);

    // stripe.createToken(register, function (status, response) {
    //   console.log( status, response );
    // });
    setProcessing(true);
    // stripe.createToken(dataForm).then((token) => {
    //   console.log("received Stripe token", token);
    // });

    const payload = await stripe
      .confirmCardPayment(clientSecret, {
        payment_method: {
          card: elements.getElement(dataForm),
        },
      })
      .then(({ paymentIntent }) => {
        //paymentIntent = payment confirmation
        db.collection("users")
          .doc(currentUser?.uid)
          .collection("orders")
          .doc(paymentIntent.id)
          .set({
            cart: [
              cart.brand,
              cart.descriptionTitle,
              cart.price,
              cart.quantity,
            ],
            amount: paymentIntent.amount,
            created: paymentIntent.created,
          });

        setSucceeded(true);
        setError(null);
        setProcessing(false);

        clearCart();
      });

    setPopup(true);
    setTimeout(() => {
      setPopup(false);
      history.replace("/orders");
    }, 3000);
  };

  const onErrors = (errors) => alert("Complete the form");

  console.log("THE SECRETTTTT", clientSecret);
  console.log("VALUE", dataForm);

  const iframeStyles = {
    base: {
      color: "#000000",

      fontSize: "16px",
      iconColor: "#fff",
      "::placeholder": {
        color: "#87bbfd",
      },
    },
    invalid: {
      iconColor: "#FFC7EE",
      color: "#FFC7EE",
    },
    complete: {
      iconColor: "#cbf4c9",
    },
  };

  const cardElementOpts = {
    iconStyle: "solid",
    style: iframeStyles,
    hidePostalCode: true,
  };

  return (
    <div className="paymentPage__container">
      <div className="paymentPage__subContainer">
        <div className="addressPage__header">
          <img alt="" src={HeaderImage1}></img>
        </div>
        <h1 className="paymentPage__title">Select payment method</h1>
        <hr />
        {}
        <hr />
        <form onSubmit={handleSubmit(onFormSubmit, onErrors)}>
          <div className="paymentPage__firstRow">
            <span>Name on Card</span>
            <input
              {...register("cardName", { required: true })}
              onChange={handleChange}
              type="text"
              placeholder=""
              name="cardName"
            ></input>
            <span>Card Number</span>
            <input
              {...register("cardNumber", { required: true })}
              onChange={handleChange}
              type="text"
              placeholder=""
              name="cardNumber"
            ></input>
            <span>Expiration Date</span>
            <div className="expireDate__container">
              <div className="expireDate__month">
                <span>Month</span>
                <input
                  {...register("month", { required: true })}
                  onChange={handleChange}
                  type="text"
                  placeholder=""
                  name="month"
                ></input>
              </div>
              <div className="expireDate__year">
                <span>Year</span>
                <input
                  {...register("year", { required: true })}
                  onChange={handleChange}
                  type="text"
                  placeholder=""
                  name="year"
                ></input>
              </div>
            </div>
          </div>
          <div className="paymentPage__secondRow">
            <div className="paymentPage__inputContainer">
              <span>CCV</span>
              <input
                {...register("ccv", { required: true })}
                onChange={handleChange}
                className="paymentPage__cityInput"
                type="text"
                placeholder=""
                name="ccv"
              ></input>
            </div>
            {/* <CardElement options={CARD_OPTIONS} /> */}
          </div>
          <CardElementContainer>
            <CardElement
              options={cardElementOpts}
              // onChange={handleCardDetailsChange}
            />
          </CardElementContainer>
          <button
            // onClick={() => clearCart()}
            className="paymentPage__button"
            type="submit"
            value="Submit Form"
          >
            Place order
          </button>
          <Popup trigger={popup} setTrigger={setPopup} />
        </form>
        <div className="paymentPage__buttonContainer"></div>
      </div>
    </div>
  );
};

const mapDispatchToProps = (dispatch) => ({
  clearCart: () => dispatch(clearCart()),
});

const mapStateToProps = () =>
  createStructuredSelector({
    currentUser: selectCurrentUser,
    total: selectCartTotal,
    cart: selectCartItems,
  });

export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(PaymentPage)
);
