import React, { useState } from "react";

import HeaderImage from "../../assets/address-page-header.png";

import { Dropdown } from "semantic-ui-react";
import _ from "lodash";
import faker from "faker";

import "./address-page.styles.css";
import { countryOptions } from "./address-page.data";

const addressDefinitions = faker.definitions.address;
const stateOptions = _.map(addressDefinitions.state, (state, index) => ({
  key: addressDefinitions.state_abbr[index],
  text: state,
  value: addressDefinitions.state_abbr[index],
}));

const AdressPage = () => {
  const [country, setCountry] = useState(null);
  const [name, setName] = useState(null);
  const [phone, setPhone] = useState(null);
  const [nameStreet, setNameStreet] = useState(null);
  const [numberStreet, setNumberStreet] = useState(null);
  const [city, setCity] = useState(null);
  const [state, setState] = useState(null);
  const [zipCode, setZipCode] = useState(null);

  const handleChange = (_e, { aqui }) => {
    setCountry({ aqui });
  };

  console.log(country);
  return (
    <div className="addressPage__container">
      <div className="addressPage__subContainer">
        <div className="addressPage__header">
          <img alt="" src={HeaderImage}></img>
        </div>
        <h1 className="addressPage__title">Select a shipping address</h1>
        <hr />
        ADDRESS CARD
        <hr />
        <div className="addressPage__firstRow">

          
          <h3>Add new Address</h3>

          <form onSubmit={this.handleSubmit}>



               
</form>

          <span>Country/Region</span>
          <Dropdown
            value={country}
            onChange={(country) => setCountry(country.target.value)}
            placeholder="Select Country"
            search
            selection
            options={countryOptions}
            className="addressPage__dropdown"
          />
         
          <span>Full name (First and Last name</span>
          <input
            value={name}
            onChange={(name) => setName(name.target.value)}
            type="text"
            placeholder=""
          ></input>

          <span>Phone number</span>
          <input
            value={phone}
            onChange={(phone) => setPhone(phone.target.value)}
            type="text"
            placeholder=""
          ></input>

          <span>Address</span>
          <input
            value={nameStreet}
            onChange={(nameStreet) => setNameStreet(nameStreet.target.value)}
            type="text"
            placeholder="Street address or P.O. Box"
          ></input>
          <input
            value={numberStreet}
            onChange={(numberStreet) =>
              setNumberStreet(numberStreet.target.value)
            }
            type="text"
            placeholder="Apt, suite, unit, building, floor, etc."
          ></input>
        </div>
        <div className="addressPage__secondRow">
          <div className="addressPage__inputContainer">
            <span>City</span>
            <input
              value={city}
              onChange={(city) => setCity(city.target.value)}
              className="addressPage__cityInput"
              type="text"
              placeholder=""
            ></input>
          </div>

          <div className="addressPage__inputContainer">
            <span>State</span>
            <Dropdown
              value={state}
              onChange={(state) => setState(state.target.value)}
              placeholder="State"
              search
              selection
              options={stateOptions}
              className={"addressPage__stateInput"}
            />
          </div>
          <div className="addressPage__inputContainer">
            <span>ZIP Code</span>
            <input
              value={zipCode}
              onChange={(zipCode) => setZipCode(zipCode.target.value)}
              className="addressPage__zipInput"
              type="text"
              placeholder=""
            ></input>
          </div>
        </div>
        <div className="addressPage__buttonContainer">
          <button type="submit" value="Submit Form">
            Use this Address
          </button>
        </div>
      </div>
    </div>
  );
};

export default AdressPage;
