import React from "react";
import { withRouter } from "react-router-dom";

import { connect } from "react-redux";
import { addItem } from "../../redux/cart/cart.action";

import CustomButton from "../custom-button/custom-button.component";

import {
  CollectionItemContainer,
  InfoContainer,
  ImageContainer,
  DescriptionContainer,
  RatingContainer,
  PriceContainer,
} from "./collection-item.styles";

const CollectionItem = ({ history, match, item, addItem }) => {
  const { linkUrl, imageUrl, description, rating, price } = item;
  return (
    <CollectionItemContainer className="cart-item">
      <InfoContainer className="product__info">
        {/* <p>{title}</p> */}
      </InfoContainer>
      <div onClick={() => history.push(`${match.url}${linkUrl}`)}>
        <ImageContainer
          className="collectionitem__image"
          src={imageUrl}
          alt=""
        />
      </div>

      <DescriptionContainer className="collectionItem__description">
        {description}
      </DescriptionContainer>
      <RatingContainer className="product__rating">
        {Array(rating)
          .fill()
          .map((_, i) => (
            <p>‚≠ê</p>
          ))}
      </RatingContainer>
      <PriceContainer className="product__price">
        <small>$</small>
        <strong>{price}</strong>
      </PriceContainer>
      <CustomButton onClick={() => addItem(item)}>Add to Cart</CustomButton>
    </CollectionItemContainer>
  );
};

const mapDispatchToProps = (dispatch) => ({
  addItem: (item) => dispatch(addItem(item)),
});

export default withRouter(connect(null, mapDispatchToProps)(CollectionItem));
