import React from "react";
import CONTENT__DATA from "../sidenav-content/sidenav-content.data";

import { MdKeyboardArrowRight } from "react-icons/md";

import "./sidenav-row.styles.css";

import { useAmazonContext } from "../../context/amazon-context";
import { useState } from "react";

const SideNavRow = (props) => {
  const { setSubContainer, setSubContainerEntries } = useAmazonContext();

  const openRow = () => {
    setSubContainer(true);
    setSubContainerEntries(props.entries);
  };

  return (
    <div className="sidenavRow" onClick={() => props.entries && openRow()}>
      <div>{props.text}</div>
      <span className="siderow__arrow">
        <MdKeyboardArrowRight size={25} />
      </span>
    </div>
  );
};

export default SideNavRow;
