import React from "react";
import { withRouter } from "react-router-dom";

import { connect } from "react-redux";
import { addItem } from "../../redux/cart/cart.action";

import CustomButton from "../custom-button/custom-button.component";

import "./collection-item.styles.css";

const CollectionItem = ({ history, match, item, addItem }) => {
  const { linkUrl, imageUrl, description, rating, price } = item;
  return (
    <div className="cart-item">
      <div className="product__info">{/* <p>{title}</p> */}</div>
      <div onClick={() => history.push(`${match.url}${linkUrl}`)}>
        <img className="collectionitem__image" src={imageUrl} alt="" />
      </div>

      <p className="collectionItem__description">{description}</p>
      <div className="product__rating">
        {Array(rating)
          .fill()
          .map((_, i) => (
            <p>‚≠ê</p>
          ))}
      </div>
      <p className="product__price">
        <small>$</small>
        <strong>{price}</strong>
      </p>
      <CustomButton additem={addItem(item)} name={"Add to Basket"} />
    </div>
  );
};

const mapDispatchToProps = (dispatch) => ({
  addItem: (item) => dispatch(addItem(item)),
});

export default withRouter(connect(null, mapDispatchToProps)(CollectionItem));
