import React, { useState } from "react";

import HamburgerButton from "../hamburger-button/hamburger-button.component";
import Sidenav from "../sidenav/sidenav.component";
import { Transition } from "react-transition-group";

import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { setSubContainer } from "../../redux/sidenav/sidenav.actions";
import { selectEntryStore } from "../../redux/sidenav/sidenav.selectors";

import {
  ComponentContainer,
  ButtonContainer,
  Overlay,
  CloseBtn,
  SubheaderUl,
  ListContainer,
  Li,
} from "./subheader.styles";

const SubHeader = ({ entryStore, setSubContainer }) => {
  const [navOpen, setNavOpen] = useState(false);

  const openNav = () => {
    setNavOpen(true);
  };

  const closeNav = () => {
    setNavOpen(false);
  };

  return (
    <ComponentContainer className="subheader">
      <ButtonContainer className="subheader__container">
        <HamburgerButton click={openNav} />
      </ButtonContainer>
      <Transition
        in={navOpen && entryStore}
        timeout={300}
        mountOnEnter
        unmountOnExit
      >
        {(state) => {
          if (state === "exited") setSubContainer(false);
          return (
            <div>
              <Sidenav state={state} />
              <Overlay
                className="subheader__overlay"
                props={state}
                // style={
                //   state === "entering"
                //     ? { animation: "show .3s forwards" }
                //     : state === "entered"
                //     ? { opacity: "1" }
                //     : { animation: "show .3s backwards reverse" }
                // }
                onClick={closeNav}
              ></Overlay>
              <CloseBtn
                className="closeBtn"
                props={state}
                style={
                  state === "entering"
                    ? { animation: "show .3s forwards" }
                    : state === "entered"
                    ? { opacity: "1" }
                    : { animation: "show .3s backwards reverse" }
                }
                onClick={closeNav}
              >
                &times;
              </CloseBtn>
            </div>
          );
        }}
      </Transition>

      <SubheaderUl className="subheader__ul">
        <ListContainer className="subheader__container">
          <Li>Today's Deals</Li>
        </ListContainer>
        <ListContainer className="subheader__container">
          <Li>Customer Servive</Li>
        </ListContainer>
        <ListContainer className="subheader__container">
          <Li>Gifst Cards</Li>
        </ListContainer>
        <ListContainer className="subheader__container">
          <Li>Registry</Li>
        </ListContainer>
        <ListContainer className="subheader__container">
          <Li>Sell</Li>
        </ListContainer>
      </SubheaderUl>
    </ComponentContainer>
  );
};

const mapStateToProps = (state) =>
  createStructuredSelector({
    entryStore: selectEntryStore,
  });

const mapDispatchToProps = (dispatch) => ({
  setSubContainer: (state) => dispatch(setSubContainer(state)),
});

export default connect(mapStateToProps, mapDispatchToProps)(SubHeader);
