import styled from "styled-components";

export const ComponentContainer = styled.div`
  width: 100%;
  height: 50px;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #37475a;
  color: white;
  cursor: pointer;

  position: relative;
  top: -200px;
`;

export const Title = styled.span`
  font-size: 13px;
`;
