import React from "react";
import { withRouter } from "react-router-dom";

import { connect } from "react-redux";
import { addItem } from "../../redux/cart/cart.action";

import CustomButton from "../custom-button/custom-button.component";

import {
  ComponentContainer,
  ComponentSubcontainer,
  Info,
  Orange,
  Image,
  Title,
  Rating,
  PriceContainer,
  Price,
  Symbol,
  ButtonContainer,
} from "./collection-item.styles";

const CollectionItem = ({ history, match, item, addItem }) => {
  const { linkUrl, imageUrl, descriptionTitle, rating, price, amazonChoice } =
    item;
  return (
    <ComponentContainer className="cart-item">
      {amazonChoice ? (
        <Info>
          Amazon`s <Orange>Choice</Orange>
        </Info>
      ) : (
        ""
      )}
      <ComponentSubcontainer>
        <Image
          onClick={() => history.push(`${match.url}${linkUrl}`)}
          className="collectionitem__image"
          src={imageUrl}
          alt=""
        />
        <Title className="collectionItem__description">
          {descriptionTitle}
        </Title>
        <Rating className="product__rating">
          {Array(rating)
            .fill()
            .map((_, i) => (
              <p>⭐</p>
            ))}
        </Rating>
        <PriceContainer className="product__price">
          <Price>{price}</Price>
          <Symbol>€</Symbol>
        </PriceContainer>
        {/* <ButtonContainer>
        <CustomButton collectionButton onClick={() => addItem(item)}>
          Add to Cart
        </CustomButton>
      </ButtonContainer> */}
      </ComponentSubcontainer>
    </ComponentContainer>
  );
};

const mapDispatchToProps = (dispatch) => ({
  addItem: (item) => dispatch(addItem(item)),
});

export default withRouter(connect(null, mapDispatchToProps)(CollectionItem));
