import React from "react";
import { connect } from "react-redux";

import ProductDetailsView from "../product-details-view/product-details-view.component";

import {
  selectProductDeteails,
  selectCollection,
  selectCollections,
  mapSelecProductDetails,
  algo,
} from "../../redux/products/product.selectors";

import "./product-details.styles.css";
import { Collections } from "@material-ui/icons";

const ProductDetails = ({ collection, history }) => {
  console.log("PRODUCT DETAILS", collection);

  const extractUrl = () => {
    const a = history.location.pathname;
    const b = a.split("/")[2];
    return console.log("PICA", b);
  };

  const mapFunction = (collection) => {
    const c = extractUrl();
    for (let i = 0; i < collection.length; i++) {
      if (collection[i].tag === c) {
        return console.log("Estoy dentro");
      } else {
        return console.log("no ha funcionado");
      }
    }
  };

  return (
    <div onClick={mapFunction(collection)}>
      PRODUCT DETAILS return <ProductDetailsView item={collection} />;
    </div>
  );
};

const mapStateToProps = (state, ownProps) => {
  console.log("KIWI", ownProps.match.url);
  return {
    collection: selectProductDeteails(ownProps.match.url)(state),
  };
};

export default connect(mapStateToProps)(ProductDetails);
