import React from "react";
import { withRouter } from "react-router-dom";

import { connect } from "react-redux";
import { addItem } from "../../redux/cart/cart.action";

import CustomButton from "../custom-button/custom-button.component";

import {
  ComponentContainer,
  Info,
  ImageContainer,
  DescriptionContainer,
  RatingContainer,
  PriceContainer,
  CustomButtonContainer,
} from "./collection-item.styles";

const CollectionItem = ({ history, match, item, addItem }) => {
  const { linkUrl, imageUrl, descriptionTitle, rating, price } = item;
  return (
    <ComponentContainer className="cart-item">
      <div onClick={() => history.push(`${match.url}${linkUrl}`)}>
        <ImageContainer
          className="collectionitem__image"
          src={imageUrl}
          alt=""
        />
      </div>

      <DescriptionContainer className="collectionItem__description">
        {descriptionTitle}
      </DescriptionContainer>
      <RatingContainer className="product__rating">
        {Array(rating)
          .fill()
          .map((_, i) => (
            <p>‚≠ê</p>
          ))}
      </RatingContainer>
      <PriceContainer className="product__price">
        <small>$</small>
        <strong>{price}</strong>
      </PriceContainer>
      <CustomButtonContainer>
        <CustomButton collectionButton onClick={() => addItem(item)}>
          Add to Cart
        </CustomButton>
      </CustomButtonContainer>
    </ComponentContainer>
  );
};

const mapDispatchToProps = (dispatch) => ({
  addItem: (item) => dispatch(addItem(item)),
});

export default withRouter(connect(null, mapDispatchToProps)(CollectionItem));
