import styled, { css } from "styled-components";

import { keyframes } from "styled-components";

export const DropdownAnimations = keyframes`
    0% {
        opacity: 0;
        transform: scaleY(0);
    }
    100% {
        opacity: 1;
        transform: scaleY(1);
    }
`;

const enteringStyles = css`
  animation-name: ${DropdownAnimations};
  animation-duration: 0.3s;
  animation-direction: forwards;
  height: `&{}`
`;

const enteredStyles = css`
  transform: scaleY(1);
  opacity: 1;
`;

const getDropwdownStyles = (props) => {
  if (props.isEntering) return enteringStyles;
};

export const DropdownContainer = styled.div`
  height: 0px;
  transition: height 0.3s;
  transform-origin: center top;

  ${getDropwdownStyles}
`;

export const SidenavDropdown = styled.div`
  display: flex;
  width: 100%;
  align-items: center;
  padding: 15px 25px 15px 25px;
  cursor: pointer;

  &:hover {
    background-color: #eaeded;
  }
`;

export const DropdownArrow = styled.div`
  display: flex;
  justify-content: center;
`;
