import React from "react";

import SideNavRow from "../siderow/sidenav-row.component";

import { MdArrowBack } from "react-icons/md";

import "./subcontainer.styles.css";

import { useAmazonContext } from "../../context/amazon-context";

import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { selectSubContainerEntries } from "../../redux/sidenav/sidenav.selectors";
import { setSubContainerEntries } from "../../redux/sidenav/sidenav.actions";

const SubContainer = (
  { subContainerEntries, setSubContainer },
  props
) => {
  // const { subContainerEntries, setSubContainer } = useAmazonContext();
  console.log("SUBCONThereeeeAINER", subContainerEntries);
  return (
    <div
      // className={`${props.style === "entering" ? "": "" } "sub-container"`}
      className="sub-container"
      style={
        props.state === "entering"
          ? { animation: "moveSubContainer .3s forwards" }
          : props.state === "entered"
          ? { transform: "translateX(0px)" }
          : { animation: "moveSubContainer .3s reverse backwards" }
      }
    >
      <div className="sub-header" onClick={() => setSubContainerEntries(false)}>
        <div className="subheader__title">
          <MdArrowBack size={20} />
          <span>MAIN MENU</span>
        </div>
      </div>
      {subContainerEntries.map((subEntry) => (
        <>
          <div className="sidenavContentHeader">{subEntry.title}</div>
          {subEntry.entries.map((superEntry) => (
            <SideNavRow text={superEntry.title} />
          ))}
        </>
      ))}
      <div style={{ minHeight: "60px" }}></div>
    </div>
  );
};

const mapStateToProps = (state) =>
  createStructuredSelector({
    subContainerEntries: selectSubContainerEntries,
  });

const mapDispatchToProps = (dispatch) => ({
  setSubContainerEntries: (state) => dispatch(setSubContainerEntries(state)),
});

export default connect(mapStateToProps, mapDispatchToProps)(SubContainer);
