import React, { useState } from "react";
import { useEffect } from "react";
import { connect } from "react-redux";

import ReactImageMagnify from "react-image-magnify";

import "./product-details.styles.css";

const ProductDetails = ({ collection, history }) => {
  const [index, setIndex] = useState(0);
  const fullDateNow = new Date().toDateString();
  const dateNow = fullDateNow.slice(0, 7);

  console.log("ARRA", dateNow);
  console.log("full date", fullDateNow);

  useEffect(() => {
    console.log("QUE ES COLLECTION", collection);
    setIndex(getIndex());
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const getIndex = () => {
    const urlPath = history.location.pathname;
    const urlPathId = urlPath.split("/")[2];

    for (let i = 0; i < collection.length; i++) {
      if (collection[i].linkUrl === `/${urlPathId}`) {
        return i;
      }
    }
  };

  const getRandomNumber = () => {
    return Math.floor(Math.random() * 5 + 1);
  };

  const {
    imageUrl,
    zoomImage,
    descriptionTitle,
    fullDescription,
    price,
    rating,
  } = collection[index];

  return (
    <div className="reviewProduct__container">
      <div className="reviewProduct__subContainer">
        <div className="reviewProduct__leftSide">
          LEFT SIDE
          <div className="reviewProduct__imageContainer">
            {/* {<img alt="" src={imageUrl} />} */}
            {
              <ReactImageMagnify
                {...{
                  smallImage: {
                    alt: "",
                    isFluidWidth: true,
                    src: imageUrl,
                  },
                  largeImage: {
                    src: zoomImage,
                    width: 1200,
                    height: 1800,
                  },
                  enlargedImageContainerDimensions: {
                    width: "200%",
                    height: "200%",
                  },
                }}
              />
            }
          </div>
        </div>

        <div className="reviewProduct__rightSide">
          RIGHT SIDE
          <div className="reviewProduct__title">{descriptionTitle}</div>
          <div className="reviewProduct__rating">
            {" "}
            {Array(rating)
              .fill()
              .map((_, i) => (
                <p>⭐</p>
              ))}
          </div>
          <hr className="reviewProduct__line" />
          <div className="reviewProduct__fullDescription">
            {fullDescription.map((paragrah) => {
              return (
                <ul>
                  <li className="reviewProduct__fullDescriptionList">
                    {paragrah}
                  </li>
                </ul>
              );
            })}
          </div>
        </div>

        <div className="reviewProduct__paymentCardContainer">
          <div className="reviewProduct__paymentSubCardContainer">
            {price}€
            <span>
              Arrives:{dateNow} {getRandomNumber}
            </span>
            <button className="addToCart__button">Add to Cart</button>
            <button className="bunNow__button">Buy Now</button>
          </div>
        </div>
      </div>
    </div>
  );
};

