import { createSelector } from "reselect";

const selectProduct = (state) => state.product;

export const selectCollections = createSelector([selectProduct], (product) => {
  console.log("Product", product);
  return product.collections;
});

export const selectCollectionsForPreview = createSelector(
  [selectCollections],
  (collections) => {
    return collections
      ? Object.keys(collections).map((key) => collections[key])
      : [];
  }
);

export const selectCollection = (collectionUrlParam) =>
  createSelector([selectCollections], (collections) => {
    console.log(
      "collenctions[collectionUrlParam",
      collections[collectionUrlParam]
    );
    return collections ? collections[collectionUrlParam] : null;
  });

export const selectProductDeteails = (urlParam) =>
  createSelector([selectCollections], (collections) => {
    console.log("POPO", urlParam);
    const a = urlParam.split("/")[1];
    console.log("estoy dentro de selec");
    return collections ? collections[a].items : null;
  });

export const mapSelecProductDetails = createSelector(
  [selectProductDeteails],

  (productView) => {
    productView.map((view) => {
      return view;
    });
  }
);

export const selectorAlgo = createSelector([mapSelecProductDetails], (algo) => {
  return algo;
});

export const selectSpecificProduct = (urlParam) =>
  createSelector([selectCollections], (collections) => {
    const a = urlParam.split("/")[1];
    const b = collections[a].items;
    const c = urlParam.split("/")[2];
    console.log("que es esto", b[0].linkUrl);
    for (let i = 0; i < b.length; i++) {
      if (b[i].tag === c) {
        console.log("Estoy dentro");
        return b[i];
      } else {
        return console.log("no ha funcionado");
      }
    }
  });
