import React from "react";
import { connect } from "react-redux";
import {
  addItem,
  clearItemFromCart,
  removeItem,
} from "../../redux/cart/cart.action";

import "./checkout-payment-item.styles.css";

const CheckOutPaymentItem = ({ cartItem }) => {
  const { imageUrl, descriptionTitle, rating, price } = cartItem;
  return (
    <div className="paymentItem__container">
      <div className="paymentItem__title">{descriptionTitle}</div>
      <div className="paymentItem__price">{price}â‚¬</div>
      <img alt="" src={imageUrl}></img>
      <div className="arrow" onClick={() => removeItem(cartItem)}>
        &#10094;
      </div>
      <span className="value">{quantity}</span>
      <div className="arrow" onClick={() => addItem(cartItem)}>
        &#10095;
      </div>
    </div>
  );
};

const mapToDispatchToProps = (dispatch) => ({
  addItem: (item) => dispatch(addItem(item)),
  clearItem: (item) => dispatch(clearItemFromCart(item)),
  removeItem: (item) => dispatch(removeItem(item)),
});

export default connect(null, mapToDispatchToProps)(CheckOutPaymentItem);
