import styled from "styled-components";

export const ComponentContainer = styled.div`
  padding: 40px;
  margin: 20px 0;
  border: 1px solid lightgray;
  background-color: white;
  position: relative;
`;

export const OrderId = styled.p`
  position: absolute;
  top: 40px;
  right: 20px;
  font-weight: 500;
`;

export const Total = styled.h3`
  font-weight: 500;
  text-align: right;
`;
