import firebase from "firebase/app";
import "firebase/firestore";
import "firebase/auth";

const firebaseConfig = {
  apiKey: "AIzaSyDEGffR0M4Rf29pntl9tsiXmBcLlYLF_Yo",
  authDomain: "clone-2c3b0.firebaseapp.com",
  projectId: "clone-2c3b0",
  storageBucket: "clone-2c3b0.appspot.com",
  messagingSenderId: "40580541701",
  appId: "1:40580541701:web:564d3f192e2fe1c5d99b6c",
};

const firebaseApp = firebase.initializeApp(firebaseConfig);

export const db = firebaseApp.firestore();
export const auth = firebase.auth();

export const createUserProfileDocumet = async (userAuth, additionalData) => {
  if (!userAuth) return;

  const userRef = firebaseApp.doc(`users/${userAuth.uid}`);
  const snapShot = await userRef.get();

  if (!snapShot.exists) {
    const { displayName, email } = userAuth;
    const createAt = new Date();

    try {
      await userRef.set({
        displayName,
        email,
        createAt,
        ...additionalData,
      });
    } catch (error) {
      console.log("error creating user", error.message);
    }
  }
  return userRef;
};
