import React, { useState } from "react";

import HamburgerButton from "../hamburger-button/hamburger-button.component";
import Sidenav from "../sidenav/sidenav.component";
import { Transition } from "react-transition-group";
import { useAmazonContext } from "../../context/amazon-context";

import "./subheader.styles.css";

const SubHeader = () => {
  const [navOpen, setNavOpen] = useState(false);
  const { setSubContainer } = useAmazonContext();

  const openNav = () => {
    setNavOpen(true);
  };

  const closeNav = () => {
    setNavOpen(false);
  };

  const { entryStore } = useAmazonContext();
  return (
    <div className="subheader">
      <div className="subheader__container">
        <HamburgerButton click={openNav} />
      </div>
      <Transition
        in={navOpen && entryStore}
        timeout={300}
        mountOnEnter
        unmountOnExit
      >
        {(state) => {
          if (state === "exited") setSubContainer(false);
          return (
            <div>
              <Sidenav state={state} />
              <div
                className="subheader__overlay"
                style={
                  state === "entering"
                    ? { animation: "show .3s forwards" }
                    : state === "entered"
                    ? { opacity: "1" }
                    : { animation: "show .3s backwards reverse" }
                }
                onClick={closeNav}
              ></div>
              <div
                className="closeBtn"
                style={
                  state === "entering"
                    ? { animation: "show .3s forwards" }
                    : state === "entered"
                    ? { opacity: "1" }
                    : { animation: "show .3s backwards reverse" }
                }
                onClick={closeNav}
              >
                {/*  ESTO ES LA X */}
                &times;
              </div>
            </div>
          );
        }}
      </Transition>

      <ul className="subheader__ul">
        <div className="subheader__container">
          <li>Today's Deals</li>
        </div>
        <div className="subheader__container">
          <li>Customer Servive</li>
        </div>
        <div className="subheader__container">
          <li>Gifst Cards</li>
        </div>
        <div className="subheader__container">
          <li>Registry</li>
        </div>
        <div className="subheader__container">
          <li>Sell</li>
        </div>
      </ul>
    </div>
  );
};

export default SubHeader;
