import styled from "styled-components";

const GetElementHeight = (props) => {
  console.log("GetElementHeight", props.height - 300);

  return props.height - 280;
};

export const ComponentContainer = styled.div`
  width: 100%;
  //height: 3196px;
  height: ${GetElementHeight}px;
  display: flex;
  flex-direction: column;
  z-index: 10;
`;

export const DirectoryMenuSubContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  z-index: 10;
  position: relative;
  top: -300px;
`;

export const DirectoryMenuFirstRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  margin: 10px 10px;
`;

export const SpecialCard = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 0 7.5px 15px;
`;

export const SpecialCardTop = styled.div`
  margin-bottom: 10px;
`;

export const SpecialCardButton = styled.div``;

export const DirectoryMenuSecondRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  margin: 10px 10px;
`;

export const MultiCarouselContainer = styled.div``;
