import React from "react";
import "./menu-item.styles.css";

const MenuItem = ({ props, advert }) => {
  const { title, imageUrl, subtitle } = props;
  return (
    <div  onClick={() => history.push(`${match.url}${linkUrl}`)} className="menu__item">
      <div className="title">
        <h2>{title}</h2>
      </div>

      <div className="menuitem__imagecontainer">
        <img className="menu__image" src={advert ? advert : imageUrl} alt="" />
      </div>
      <div className="subtitle">
        <span>{subtitle}</span>
      </div>
    </div>
  );
};

export default MenuItem;
