import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

import CarouselCard from "../carousel-card/carousel-card.component";

import "./multi-carousel.styles.css";

const responsive = {
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 5,
    slidesToSlide: 2, // optional, default to 1.
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2,
    slidesToSlide: 2, // optional, default to 1.
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1,
    slidesToSlide: 1, // optional, default to 1.
  },
};

const MultiCarousel = () => {
  return (
    <div className="">
      <Carousel
        containerClass="carousel__container"
        itemClass="carousel__item"
        sliderClass="carousel__slider"
        centerMode={true}
        showDots={true}
        responsive={responsive}
      >
        <CarouselCard image={"https://i.ibb.co/0jqHpnp/sneakers.png"} />
        <CarouselCard image={"https://i.ibb.co/0jqHpnp/sneakers.png"} />
        <CarouselCard image={"https://i.ibb.co/0jqHpnp/sneakers.png"} />
        <CarouselCard image={"https://i.ibb.co/0jqHpnp/sneakers.png"} />

        <CarouselCard image={"https://i.ibb.co/0jqHpnp/sneakers.png"} />
        <CarouselCard image={"https://i.ibb.co/0jqHpnp/sneakers.png"} />
        <CarouselCard image={"https://i.ibb.co/0jqHpnp/sneakers.png"} />
        <CarouselCard image={"https://i.ibb.co/0jqHpnp/sneakers.png"} />
      </Carousel>
    </div>
  );
};

export default MultiCarousel;
