import React, { useState } from "react";
import { useForm } from "react-hook-form";

import { withRouter } from "react-router-dom";

import Popup from "../../components/popup/popup.component";

import HeaderImage1 from "../../assets/address-page-heade1.png";

import "./payment-page.styles.css";

const PaymentPage = ({ history }) => {
  const [buttonPopup, setButtonPopup] = useState(false);
  const [timePopup, setTimePopup] = useState(false);
  const { register, handleSubmit } = useForm();

  const onFormSubmit = (data) => {
    setButtonPopup(true);
    setTimePopup(true);
    // setTimeout(() => {
    //   setTimePopup(false);
    //   history.push("/");
    // }, 3000);
  };

  const onErrors = (errors) => alert("Complete the form");

  console.log("HISOTT", history);
  return (
    <div className="addressPage__container">
      <div className="addressPage__subContainer">
        <div className="addressPage__header">
          <img alt="" src={HeaderImage1}></img>
        </div>
        <h1 className="addressPage__title">Select payment method</h1>
        <hr />
        {}
        <hr />
        <form onSubmit={handleSubmit(onFormSubmit, onErrors)}>
          <div className="addressPage__firstRow">
            <span>Name on Card</span>
            <input
              {...register("name", { required: true })}
              type="text"
              placeholder=""
            ></input>
            <span>Card Number</span>
            <input
              {...register("phone", { required: true })}
              type="text"
              placeholder=""
            ></input>
            <span>Expiration Date</span>
            <div>
              <div>
                <span>Month</span>
                <input
                  {...register("nameStreet", { required: true })}
                  type="text"
                  placeholder=""
                ></input>
              </div>
              <div>
                <span>Year</span>
                <input
                  {...register("nameStreet", { required: true })}
                  type="text"
                  placeholder=""
                ></input>
              </div>
            </div>
          </div>
          <div className="addressPage__secondRow">
            <div className="addressPage__inputContainer">
              <span>CCV</span>
              <input
                {...register("city", { required: true })}
                className="addressPage__cityInput"
                type="text"
                placeholder=""
              ></input>
            </div>
          </div>
          <button
            setTrigger={setButtonPopup}
            className="addressPage__button"
            type="submit"
            value="Submit Form"
          >
            Place order
          </button>
          <Popup trigger={buttonPopup} />
        </form>
        <div className="addressPage__buttonContainer"></div>
      </div>
    </div>
  );
};

export default withRouter(PaymentPage);
