import React from "react";
import { withRouter } from "react-router-dom";

import { connect } from "react-redux";

import {
  addItem,
  clearItemFromCart,
  removeItem,
} from "../../redux/cart/cart.action";

import {
  CheckoutContainer,
  CheckoutImage,
  CheckoutInfo,
  CheckoutTitle,
  CheckoutPrice,
  CheckoutQuantity,
  CheckoutArrow,
  CheckoutValue,
  CheckoutRating,
  CheckoutButton,
} from "./checkout.styles";

const CheckoutItem = ({
  cartItem,
  hideButton,
  clearItem,
  addItem,
  removeItem,
  history,
  match,
}) => {
  const {
    imageUrl,
    title,
    price,
    rating,
    quantity,
    descriptionTitle,
    linkUrl,
  } = cartItem;
  console.log("PRUEBA", history);
  return (
    <CheckoutContainer className="checkoutProduct">
      <CheckoutImage
        onClick={() => history.push(`shopcategory${linkUrl}`)}
        className="checkoutProduct__image"
        src={imageUrl}
        alt=""
      />

      <CheckoutInfo className="checkoutProduct__info">
        <CheckoutTitle className="checkoutProduct__title">hola</CheckoutTitle>
        <CheckoutPrice className="checkoutProduct__price">
          <p>{descriptionTitle}</p>
          <CheckoutQuantity className="quantity">
            <CheckoutArrow
              className="arrow"
              onClick={() => removeItem(cartItem)}
            >
              &#10094;
            </CheckoutArrow>
            <CheckoutValue className="value">{quantity}</CheckoutValue>
            <CheckoutArrow className="arrow" onClick={() => addItem(cartItem)}>
              &#10095;
            </CheckoutArrow>
          </CheckoutQuantity>
          <small>$</small>
          <strong>{price}</strong>
        </CheckoutPrice>
        <CheckoutRating className="checkoutProduct__rating">
          {Array(rating)
            .fill()
            .map((_, i) => (
              <p>⭐</p>
            ))}
        </CheckoutRating>
        {!hideButton && (
          <CheckoutButton onClick={() => clearItem(cartItem)}>
            Remove from Basket
          </CheckoutButton>
        )}
      </CheckoutInfo>
    </CheckoutContainer>
  );
};

const mapToDispatchToProps = (dispatch) => ({
  addItem: (item) => dispatch(addItem(item)),
  clearItem: (item) => dispatch(clearItemFromCart(item)),
  removeItem: (item) => dispatch(removeItem(item)),
});

export default connect(null, mapToDispatchToProps)(CheckoutItem);
