import React from "react";

import { MdKeyboardArrowRight } from "react-icons/md";

import "./sidenav-row.styles.css";

import { useAmazonContext } from "../../context/amazon-context";

import { connect } from "react-redux";
import {
  setSubContainer,
  setSubContainerEntries,
} from "../../redux/sidenav/sidenav.actions";

const SideNavRow = ({ setSubContainer, setSubContainerEntries }, props) => {
  // const { setSubContainer, setSubContainerEntries } = useAmazonContext();

  const openRow = () => {
    setSubContainer(true);
    setSubContainerEntries(props.entries);
  };

  return (
    <div className="sidenavRow" onClick={() => props.entries && openRow()}>
      <div>{props.text}</div>
      <span className="siderow__arrow">
        <MdKeyboardArrowRight size={25} />
      </span>
    </div>
  );
};

const mapDispatchToProps = (dispatch) => {
  setSubContainer : (state) => setSubContainer(state);
  setSubContainerEntries : (entries) => setSubContainerEntries(entries);
};

export default connect(null, mapDispatchToProps)(SideNavRow);
