import React from "react";
import SideNavContent from "../sidenav-content/sidenav-content.component";
import SubContainer from "../subcontainer/subcontainer.component";

import CONTENT__DATA from "../sidenav-content/sidenav-content.data";
import { Transition } from "react-transition-group";
import "./sidenav.styles.css";

const SideNav = (props) => {
  const { subContainer, setSubContainer } = CONTENT__DATA;
  return (
    <div
      className="sidenav"
      style={
        props.state === "entering"
          ? { animation: "moveSideBar .3s forwards" }
          : props.state === "entered"
          ? { transform: "translateX(-0px)" }
          : { animation: "moveSideBar .3s reverse backwards" }
      }
    >
      <div className="sidenavHeader">
        <i className="fas fa-user-circle"></i> Hello, Sign In
      </div>
      <Transition 
        in={!subContainer} 
        timeout={300} 
        unmountOnExit 
        mountOnEnter>
        {(state) => (
          <SideNavContent state={state} />
        
        )}    
      </Transition>
      <Transition 
        in={subContainer} 
        timeout={300} 
        unmountOnExit 
        mountOnEnter>
        {(state) => (
          <SubContainer state={state} setSubContainer={setSubContainer} />
        )}
      </Transition>
    </div>
  );
};

export default SideNav;
