import styled from "styled-components";

export const ComponentContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

export const Row = styled.div``;

export const Span = styled.span`
  font-size: 13px;
  margin-right: 5px;
`;

export const Button = styled.button`
  width: 180px;
  height: 40px;
  margin-top: 10px;
  cursor: pointer;
`;
