import React from "react";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";

import CarouselCard from "../carousel-card/carousel-card.component";
import CAROUSEL__DATA from "./multi-carousel.data";

import "./multi-carousel.styles.css";

const responsive = {
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 5,
    slidesToSlide: 2, // optional, default to 1.
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2,
    slidesToSlide: 2, // optional, default to 1.
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1,
    slidesToSlide: 1, // optional, default to 1.
  },
};

class MultiCarousel extends React.Component {
  constructor() {
    super();

    this.state = {
      carousel: CAROUSEL__DATA,
    };
  }

  render() {
    return (
      <div className="">
        <Carousel centerMode={true} showDots={true} responsive={responsive}>
          {this.state.carousel.map(({ id, title, image, items }) => {
            return (
              <CarouselCard id={id} title={title} image={image} items={items} />
            );
          })}
        </Carousel>
      </div>
    );
  }
}

export default MultiCarousel;
