import styled from "styled-components";

export const SigninCardContainer = styled.div`
  width: 350px;
  max-width: 350px;
  height: 115px;
  background-color: white;
  padding-top: 10px;
  padding-bottom: 15px;

  display: flex;
  flex-direction: column;
  align-items: center;

  justify-content: center;
  align-items: center;
`;

export const SigninCardSubcontainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;
