import React from "react";
import { Route } from "react-router-dom";
import { connect } from "react-redux";

import { selectCollection } from "../../redux/products/product.selectors";

import CollectionItem from "../../components/collection-item/collection-item.component";
import Header from "../../components/header/header.component";
import Footer from "../../components/footer/footer.component";

import CollectionOverviewContainer from "../../components/collection-overview/collection-overview.component";
import ProductDetails from "../../components/product-details/product-details.component";

import "./productpage.styles.css";

const ProductPage = ({ match, history }) => {
  console.log("AQQQ", history);

  return (
    <div className="shop-page">
      <Route
        exact
        path={`${match.path}`}
        component={CollectionOverviewContainer}
      />
      <Route
        path={`${match.path}/:collectionId`}
        component={ProductDetails}
        //render={props => (
        //<CollectoinPageWithSpinner isLoading={!isCollectionsLoaded} {...props}/>
        //)}
      >
        {console.log("mira Aqui", `${match.path}/:collectionId`)}
      </Route>
    </div>
  );
};

const mapStateToProps = (state, ownProps) => {
  console.log("jiujiu", ownProps.match.params.collectionId);
  return {
    collection: selectCollection(ownProps.match.params.collectionId)(state),
  };
};

export default connect(mapStateToProps)(ProductPage);
