import React from "react";
import { connect } from "react-redux";

import {
  addItem,
  removeItem,
  clearItemFromCart,
} from "../../redux/cart/cart.action";

import "./checkout.styles.css";

const CheckoutItem = ({ item, hideButton, clearItem }) => {
  const { id, imageUrl, title, price, rating } = item;
  return (
    <div className="checkoutProduct">
      <img className="checkoutProduct__image" src={imageUrl} alt="" />

      <div className="checkoutProduct__info">
        <p className="checkoutProduct__title">{title}</p>
        <p className="checkoutProduct__price">
          <small>$</small>
          <strong>{price}</strong>
        </p>
        <div className="checkoutProduct__rating">
          {Array(rating)
            .fill()
            .map((_, i) => (
              <p>ðŸŒŸ</p>
            ))}
        </div>
        {!hideButton && (
          <button onClick={clearItem(item)}>Remove from Basket</button>
        )}
      </div>
    </div>
  );
};

const mapToDispatchToProps = (dispatch) => ({
  addItem: (item) => dispatch(addItem(item)),
  clearItem: (item) => dispatch(clearItemFromCart(item)),
});

export default connect(null, mapToDispatchToProps)(CheckoutItem);
