import styled from "styled-components";

export const SigninCardContainer = styled.div`
  max-width: 365px;
  min-width: 280px;
  flex: 1 1 287px;
  height: 115px;
  background-color: white;
  padding-top: 10px;
  padding-bottom: 15px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

export const SigninCardSubcontainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;
