import React from "react";

import MenuItem from "../menu-item/menu-item.component";
import SigninCard from "../sign-in-card/signin-card.component";
import Ad from "../ad/ad.component";
import MultiCarousel from "../../components/multi-carousel/multi-carousel.component";
import COLLECTION__DATA from "./collections.data";

import {
  ComponentContainer,
  ComponentSubContainer,
  FirstRow,
  SpecialCard,
  SpecialCardTop,
  Button,
  SecondRow,
} from "./directory.styles";
import CAROUSEL__DATA from "../../redux/carousel/carousel.data";

class Directory extends React.Component {
  constructor(props) {
    super(props);

    this.heightRef = React.createRef();

    this.state = {
      collections: COLLECTION__DATA,
      carousel: CAROUSEL__DATA,
      height: 0,
    };
  }

  componentDidMount() {
    this.setState({
      height: this.heightRef.current.clientHeight,
    });
    this.calcularAltura();
    window.addEventListener("resize", this.calcularAltura);
  }

  calcularAltura = () => {
    if (!null) {
      return this.setState({ height: this.heightRef.current?.clientHeight });
    }
  };

  render() {
    const { advert } = this.state.collections[2];
    return (
      <ComponentContainer height={this.state.height} className="directory-menu">
        <ComponentSubContainer ref={this.heightRef}>
          <FirstRow className="first__row">
            <MenuItem props={this.state.collections[0]} />
            <MenuItem props={this.state.collections[1]} />
            <MenuItem props={this.state.collections[2]} />
            <SpecialCard className="special__card">
              <SpecialCardTop className="special__card__top">
                <SigninCard props={this.state.collections[3]} advert={advert} />
              </SpecialCardTop>
              <Button className="special__card__buttom">
                <Ad />
              </Button>
            </SpecialCard>
            <MenuItem props={this.state.collections[3]} />
            <MenuItem props={this.state.collections[4]} />
            <MenuItem props={this.state.collections[5]} />
            <MenuItem props={this.state.collections[6]} />
          </FirstRow>

          <MultiCarousel
            title={this.state.carousel["carousel1"].title} 
            carousel={this.state.carousel["carousel1"].items}
          />
          <MultiCarousel
            title={this.state.carousel["carousel2"].title}
            carousel={this.state.carousel["carousel2"].items}
          />

          <SecondRow className="second__row">
            <MenuItem props={this.state.collections[7]} />
            <MenuItem props={this.state.collections[8]} />
            <MenuItem props={this.state.collections[9]} />
            <MenuItem props={this.state.collections[10]} />
          </SecondRow>

          <MultiCarousel
            title={this.state.carousel["carousel3"].title}
            carousel={this.state.carousel["carousel3"].items}
          />
          <MultiCarousel
            collection={"ama"}
            carousel={this.state.carousel["carousel4"].items}
          />
        </ComponentSubContainer>
      </ComponentContainer>
    );
  }
}

export default Directory;
