import React from "react";

import "./dropdown-languages-card.styles.css";

const LanguageCard = ({ show }) => {
  return (
    <div className="component__container">
      <div
        className={`${
          show ? "component__subcontainer" : "component__subcontainer"
        }`}
      >
        <span className="languageCard__title"></span>
        <ul>
          <input />
        </ul>
      </div>
    </div>
  );
};

export default LanguageCard;
