import React, { useState } from "react";
import { useEffect } from "react";
import { connect } from "react-redux";

import "./product-details.styles.css";

const ProductDetails = ({ collection, history }) => {
  const [index, setIndex] = useState(0);

  useEffect(() => {
    setIndex(getIndex());
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const getIndex = () => {
    const urlPath = history.location.pathname;
    const urlPathProduct = urlPath.split("/")[2];

    for (let i = 0; i < collection.length; i++) {
      if (collection[i].tag === urlPathProduct) {
        return i;
      }
    }
  };

  const { imageUrl, title, description, name, price, rating, id } =
    collection[index];
  return (
    <div className="reviewProduct__container">
      <div className="reviewProduct__subContainer">
        <div className="reviewProduct__leftSide">
          LEFT SIDE
          <div className="reviewProduct__imageContainer">
            IMAGE CONTAINER
            {<img src={imageUrl} />}
          </div>
        </div>

        <div className="reviewProduct__rightSide">
          RIGHT SIDE
          <div className="reviewProduct__title">PRODUCT TITLE</div>
          <div>RANKING STARS</div>
          <div>PRODUCT DESCRIPTION</div>
        </div>

        <div className="reviewProduct__paymentCard">PAYMENT CARD</div>
      </div>
    </div>
  );
};

