import React from "react";
import CustomButton from "../custom-button/custom-button.component";

import "./collection-item.styles.css";

const CollectionItem = ({
  item: { imageUrl, price, name, quantity, rating, description },
}) => {
  return (
    <div className="cart-item">
      <div className="product__info">{/* <p>{title}</p> */}</div>

      <img
        onClick={() => history.push(`${match.url}${linkUrl}`)}
        className="collectionitem__image"
        src={imageUrl}
        alt=""
      />
      <p className="collectionItem__description">{description}</p>
      <div className="product__rating">
        {Array(rating)
          .fill()
          .map((_, i) => (
            <p>‚≠ê</p>
          ))}
      </div>
      <p className="product__price">
        <small>$</small>
        <strong>{price}</strong>
      </p>
      <CustomButton name={"Add to Basket"} />
    </div>
  );
};

export default CollectionItem;
