import React, { useState } from "react";
import "./header.styles.css";

import SearchIcon from "@material-ui/icons/Search";
import ShoppingBasketIcon from "@material-ui/icons/ShoppingBasket";

import { Link } from "react-router-dom";
import { connect } from "react-redux";

import { createStructuredSelector } from "reselect";
import {
  selectCartItemCount,
  selectCartTotal,
} from "../../redux/cart/cart.selectors";

import HamburgerButton from "../hamburger-button/hamburger-button.component";
import Sidenav from "../sidenav/sidenav.component";
import { Transition } from "react-transition-group";

function Header({ itemCount, total }) {
  const [navOpen, setNavOpen] = useState(false);

  const openNav = () => {
    setNavOpen(true);
  };

  const closeNav = () => {
    setNavOpen(false);
  };

  return (
    <div className="header__container">
      <div className="header">
        <Link to="/">
          <img
            alt="header__logo"
            className="header__logo"
            src="https://pngimg.com/uploads/amazon/amazon_PNG11.png"
          />
        </Link>

        <div className="header__search">
          <input className="header__searchInput" type="text" />
          <SearchIcon className="header__searchIcon" />
        </div>

        <div className="header__nav">
          <div className="header__option">
            <span className="header__optionLineOne">Hello Guest</span>
            <span className="header__optionLineOne">Sign In</span>
          </div>

          <div className="header__option">
            <span className="header__optionLineOne">Returns</span>
            <span className="header__optionLineOne">& Orders</span>
          </div>

          <div className="header__option">
            <span className="header__optionLineOne">Your</span>
            <span className="header__optionLineOne">Prime</span>
          </div>
        </div>
        <Link to="/checkout">
          <div className="header__optionBasket">
            <ShoppingBasketIcon />
            <span className="header__optionLineTwo header__basketCount">
              {itemCount}
            </span>
          </div>
        </Link>
      </div>
      <HamburgerButton onClick={this.openNav} />
      <Transition in={navOpen} timeout={300} mountOnEnter unmountOnExit>
        {(state) => {
          return (
            <>
              <Sidenav state={state} />
            </>
          );
        }}
      </Transition>
      <div className="subheader">
        <nav className="sub__nav">
          <ul className="sub__list">
            <li>Todo</li>
            <li>Todo</li>
            <li>Todo</li>
            <li>Todo</li>
            <li>Todo</li>
            <li>Todo</li>
            <li>Todo</li>
            <li>Todo</li>
            <li>Todo</li>
          </ul>
        </nav>
      </div>
    </div>
  );
}

const mapStateToProps = (state) =>
  createStructuredSelector({
    itemCount: selectCartItemCount,
    total: selectCartTotal,
  });

export default connect(mapStateToProps)(Header);
