import React, { useState } from "react";
import { useEffect } from "react";
import { connect } from "react-redux";

import { selectProductDeteails } from "../../redux/products/product.selectors";

import "./product-details.styles.css";

const ProductDetails = ({ collection, history }) => {
  const [index, setIndex] = useState(0);

  useEffect(() => {
    setIndex(getIndex());
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const getIndex = () => {
    const urlPath = history.location.pathname;
    const urlPathProduct = urlPath.split("/")[2];

    for (let i = 0; i < collection.length; i++) {
      if (collection[i].tag === urlPathProduct) {
        return i;
      }
    }
  };

  return <div>{collection[index]?.name}</div>;
};

