import React, { useState } from "react";
import { useEffect } from "react";
import { connect } from "react-redux";

import {
  selectOneProduct,
  selectCollection,
  selectProductDeteails,
} from "../../redux/products/product.selectors";

import "./product-details.styles.css";

const ProductDetails = ({ collection, history }) => {
  console.log("mira lala", collection[0].name);

  const [index, setIndex] = useState(0);

  useEffect(() => {
    const urlPath = history.location.pathname;
    const a = urlPath.split("/")[1];
    const b = collection;

    const c = urlPath.split("/")[2];
    for (let i = 0; i < b.length; i++) {
      if (b[i].tag === c) {
        console.log("Estoy dentro");
        let index = i;
        return index;
      } else {
        return console.log("no ha funcionado");
      }
    }
  }, []);

  setIndex(index);

  return <div>{collection[index].name}</div>;
};

const mapStateToProps = (state, ownProps) => {
  console.log("KIWI", ownProps.match.url);
  return {
    collection: selectProductDeteails(ownProps.match.url)(state),
  };
};

export default connect(mapStateToProps)(ProductDetails);
