import styled from "styled-components";

export const ComponentContainer = styled.div`
  max-width: 380px;
  overflow: hidden;
  width: 85%;
  height: 100%;
  position: fixed;
  z-index: 100;
  top: 0;
  left: 0;
  background-color: white;
  transform-origin: left center;
  transform: translateX(-420px);
`;

export const SidenavHeader = styled.div``;
