import React from "react";

import { connect } from "react-redux";

import "./dropdown-card.styles.css";

const DropdownCard = ({ handleAuthentication, currentUser }) => {
  const capitalize = (str) => {
    return str.charAt(0).toUpperCase() + str.slice(1);
  };

  return (
    <div className="dropdown">
      <div
        onClick={handleAuthentication}
        className="header__option dropdown__card"
      >
        <span className="header__optionLineOne">
          Hello {!currentUser ? "Guest" : capitalize(currentUser.displayName)}
        </span>
        <strong className="header__optionLineOne">Account & Lists</strong>
        <DropdownCard className="dropdown__card" />
      </div>
    </div>
  );
};

const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
});

export default connect(mapStateToProps)(DropdownCard);
