import React, { useState } from "react";
import { useEffect } from "react";
import { connect } from "react-redux";
import { addItem, toggleBuyState } from "../../redux/cart/cart.action";

import { withRouter } from "react-router-dom";

import ReactImageMagnify from "react-image-magnify";

import "./product-details.styles.css";
import { createStructuredSelector } from "reselect";
import { selectCurrentUser } from "../../redux/user/user.selectors";
import { selectCollections } from "../../redux/products/product.selectors";

const ProductDetails = ({
  collection2,
  item,
  collection,
  history,
  addItem,
  currentUser,
  toggleBuyState,
}) => {
  const [index, setIndex] = useState(0);
  const [path, setPath] = useState("");
  const fullDateNow = new Date().toDateString();
  const monthNow = fullDateNow.slice(0, 4);
  const dayNow = fullDateNow.slice(7, 10);

  useEffect(() => {
    obtainUrlData();

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const obtainUrlData = () => {
    setPath(getPath());
    setIndex(getIndex());
  };

  const getPath = () => {
    return history.location.pathname.split("/")[1];
  };

  const getIndex = () => {
    const urlPath = history.location.pathname;
    const ulrCollectionName = history.location.pathname.split("/")[1];
    const urlPathId = Number(urlPath.split("/")[2]);

    // console.log("INSIDE FUNCTION", urlPathId);
    // console.log("COLLECTION2", collection2[urlPathId]?.items);

    for (let i = 0; i < collection2[ulrCollectionName].items.length; i++) {
      if (collection2[ulrCollectionName].items[i].linkUrl === `/${urlPathId}`) {
        return i;
      }
    }
  };

  console.log("ITEMEMEMTEMTEME", path);
  console.log("USAMOS OATH", collection2[path]?.items);
  console.log("INDEXXXXXXXXX", index);

  const getRandomNumber = (dayNow) => {
    return Math.floor(
      Math.random() * (Number(dayNow) + 5 - Number(dayNow)) + Number(dayNow)
    );
  };

  const checkIfLogin = () => {
    if (currentUser) {
      history.push("/address");
    } else {
      toggleBuyState();
      history.push("/login");
    }
  };

  const {
    imageUrl,
    zoomImage,
    descriptionTitle,
    fullDescription,
    price,
    rating,
  } = collection2["computers"]?.items[0];

  return (
    <div className="reviewProduct__container">
      <div className="reviewProduct__subContainer">
        <div className="reviewProduct__leftSide">
          <div className="reviewProduct__imageContainer">
            {/* {<img alt="" src={imageUrl} />} */}
            {
              <ReactImageMagnify
                {...{
                  smallImage: {
                    alt: "",
                    isFluidWidth: true,
                    src: imageUrl,
                    imageClassName: "reviewProduct__imageContainer",
                  },
                  largeImage: {
                    src: zoomImage,
                    width: 1200,
                    height: 1800,
                  },
                  enlargedImageContainerDimensions: {
                    width: "200%",
                    height: "200%",
                  },
                }}
              />
            }
          </div>
        </div>

        <div className="reviewProduct__rightSide">
          <div className="reviewProduct__title">
            {collection2[path]?.items[index].descriptionTitle}
          </div>
          <div className="reviewProduct__rating">
            {" "}
            {Array(rating)
              .fill()
              .map((_, i) => (
                <p>⭐</p>
              ))}
          </div>
          <hr className="reviewProduct__line" />
          <div className="reviewProduct__fullDescription">
            {fullDescription
              ? fullDescription.map((paragrah) => {
                  return (
                    <ul>
                      <li className="reviewProduct__fullDescriptionList">
                        {paragrah}
                      </li>
                    </ul>
                  );
                })
              : ""}
          </div>
        </div>

        <div className="reviewProduct__paymentCardContainer">
          <div className="reviewProduct__paymentSubCardContainer">
            <span className="productDetails__price">{price}€</span>
            <span className="productDetails__date">
              Arrives:
              <strong>
                {monthNow}
                {dayNow} - {getRandomNumber(dayNow)}
              </strong>
            </span>

            <span className="reviewProudct__inStockTag">In stock soon</span>
            <span>Order it now</span>

            <div className="producDetails__buttonContainer">
              <button
                className="addToCart__button"
                // collectionButton
                onClick={() => addItem(collection2[index])}
              >
                Add to Cart
              </button>
              <button
                onClick={() => checkIfLogin(currentUser)}
                className="bunNow__button"
              >
                Buy Now
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

/* <div>{collection[index]?.name}</div>; */

// const mapStateToProps = (state, ownProps) => {
//   console.log("GRIS", ownProps);
//   return {
//     collection: selectProductDeteails(ownProps.match.url)(state),
//   };
// };

// const mapStateToProps = (state, ownProps) => {
//   console.log("GRISSSSS", ownProps);
//   return {
//     collection: selectProductDeteails(ownProps.match.url)(state),
//   };
// };

const mapStateToProps = () =>
  createStructuredSelector({
    currentUser: selectCurrentUser,
    collection2: selectCollections,
  });

const mapDispatchToProps = (dispatch) => ({
  addItem: (item) => dispatch(addItem(item)),
  toggleBuyState: () => dispatch(toggleBuyState()),
});

export default withRouter(
  connect(mapStateToProps, mapDispatchToProps)(ProductDetails)
);
