import React from "react";
import { withRouter } from "react-router-dom";

import { MenuItemContainer, MenuItemTitleContainer, MenuItemTitle, ImageContainer } from "./menu-item.sytles";

const MenuItem = ({ props, advert, history, match }) => {
  const { title, imageUrl, subtitle, linkUrl } = props;
  return (
    <div
      className="menu__item"
      onClick={() => history.push(`${match.url}${linkUrl}`)}
    >
      <div className="menuitem__title">
        <h2>{title}</h2>
      </div>

      <div className="menuitem__imagecontainer">
        <img className="menu__image" src={advert ? advert : imageUrl} alt="" />
      </div>
      <div className="menuitem__subtitle">
        <span>{subtitle}</span>
      </div>
    </div>
  );
};

export default withRouter(MenuItem);
