import React, { useState } from "react";

import { Link, useHistory } from "react-router-dom";
import { auth } from "../../firebase/firebase";

import {
  SignInContainer,
  SignInSubcontainer,
  Logo,
  Title,
  SignInInput,
  TagName,
  SignInButton,
  Description,
  SignInLink,
} from "./signin.styles";

const SignIn = () => {
  const history = useHistory();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const signIn = (e) => {
    e.preventDefault();

    auth
      .signInWithEmailAndPassword(email, password)
      .then((auth) => {
        history.push("/");
      })
      .catch((error) => alert(error.message));

    //firebase happens here
  };
  return (
    <SignInContainer className="signin">
      <Link to="/">
        <Logo
          className="signin__logo"
          alt=""
          src="https://upload.wikimedia.org/wikipedia/commons/thumb/a/a9/Amazon_logo.svg/1024px-Amazon_logo.svg.png"
        />
      </Link>
      <SignInSubcontainer className="signin__container">
        <Title>Sign-In</Title>
        <form>
          <TagName>E-mail</TagName>
          <SignInInput
            type="text"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <TagName>Password</TagName>
          <SignInInput
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <SignInButton
            type="submit"
            className="signin__signInButton"
            onClick={signIn}
          >
            Sign In
          </SignInButton>
        </form>
        <Description>
          By continuing, you agree to Amazon's Conditions of Use and Privacy
          Notice.
        </Description>
      </SignInSubcontainer>
      <h2 className="new__user">
        <span>New to Amazon?</span>
      </h2>
      <Link to="/signup">
        <Description className="signin__registerButton">
          Create your Amazon account
        </Description>
      </Link>
    </SignInContainer>
  );
};

export default SignIn;
