import { CartActionTypes } from './cart.types';

const setCurrentUser = (user) => ({
  type: "SET_CURRENT_USER",
  payload: user,
});

export default setCurrentUser;


export const removeItem = item => ({
  type: CartActionTypes.REMOVE_ITEM,
  payload: item
});
